import { Component } from '@angular/core';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {

  contador: number = 0;

  warriorBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
  }

  warriorClick() {
    document.getElementById('warrior')!.style.left = "60%";
  }

  templarioBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-5%"
  }

  templarioClick() {
    document.getElementById('templario')!.style.left = "60%";
  }

  arcanoBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-10%"
  }

  arcanoClick() {
    document.getElementById('arcano')!.style.left = "60%";
  }

  ladinoBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-14%"
  }

  ladinoClick() {
    document.getElementById('ladino')!.style.left = "60%";
  }

  druidaBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-20%"
  }

  voltarNormalBackground() {
    document.getElementById('selecao')!.style.width = "100%";
    document.getElementById('selecao')!.style.marginTop = "0%"
    document.getElementById('selecao')!.style.marginLeft = "0%"
    document.getElementById('warrior')!.style.left = "100%"
  }

}
<div class="background">
  <img id="selecao" style="width: 100%;" src="assets/img/classes.webp">
  <h1 id="title">SELECIONE SUA CLASSE</h1>
  <div id="warrior" class="marcadorDeRosto">
    <div class="conteudo">
      <h2>Renegado</h2>
      <h3>Sendo uma das classes mais simples,<br> sem precisar pensar muito.</h3>
      <h3>Normalmente agem como barbaros, so pensam em aniquilar seus inimigos com seus proprios punhos ou armas</h3>
      <h3>Nao costumam usar magia.</h3>
    </div>
  </div>
  <div id="templario" class="marcadorDeRosto">
    <div class="conteudo">
      <h2>Renegado</h2>
      <h3>Sendo uma das classes mais simples,<br> sem precisar pensar muito.</h3>
      <h3>Normalmente agem como barbaros, so pensam em aniquilar seus inimigos com seus proprios punhos ou armas</h3>
      <h3>Nao costumam usar magia.</h3>
    </div>
  </div>
  <div id="arcano" class="marcadorDeRosto">
    <div class="conteudo">
      <h2>Renegado</h2>
      <h3>Sendo uma das classes mais simples,<br> sem precisar pensar muito.</h3>
      <h3>Normalmente agem como barbaros, so pensam em aniquilar seus inimigos com seus proprios punhos ou armas</h3>
      <h3>Nao costumam usar magia.</h3>
    </div>
  </div>
  <div id="ladino" class="marcadorDeRosto">
    <div class="conteudo">
      <h2>Renegado</h2>
      <h3>Sendo uma das classes mais simples,<br> sem precisar pensar muito.</h3>
      <h3>Normalmente agem como barbaros, so pensam em aniquilar seus inimigos com seus proprios punhos ou armas</h3>
      <h3>Nao costumam usar magia.</h3>
    </div>
  </div>
</div>

<div class="warrior" (mouseenter)="warriorBackground()" (mouseleave)="voltarNormalBackground()"
  (click)="warriorClick()"></div>
<div class="templario" (mouseenter)="templarioBackground()" (mouseleave)="voltarNormalBackground()"
  (click)="warriorClick()"></div>
<div class="arcano" (mouseenter)="arcanoBackground()" (mouseleave)="voltarNormalBackground()"
  (click)="warriorClick()"></div>
<div class="ladino" (mouseenter)="ladinoBackground()" (mouseleave)="voltarNormalBackground()"
  (click)="warriorClick()"></div>
<div class="druida" (mouseenter)="druidaBackground()" (mouseleave)="voltarNormalBackground()"></div>
