import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

import { LoginForm } from '../model/loginForms.model';
import { UsuarioDTO } from '../model/usuarioDTO.model';

@Injectable({
  providedIn: 'root'
})
export class UsuarioService {



  constructor(private http: HttpClient) { }

  public logarUsuario(loginForm: LoginForm):
    Observable<UsuarioDTO> {
    return this.http.get<UsuarioDTO>(
      `/usuarios/logar?loginNome=${loginForm.nome}&loginSenha=${loginForm.senha}`
    )
  }

  @PostMapping("/cadastrar")
	public ResponseEntity<Usuario> cadastrarUsuario(
			@RequestBody @Valid RegistroForm registroForm
			) {
		return this.usuarioService.cadastrarUsuario(registroForm);
	}

}
