import { Component } from '@angular/core';
import { MecanicasServiceService } from '../mecanicas-service.service';
import { Renegado } from '../shared/renegado/renegado.model';
import { MonstroFacil } from '../shared/monstroFacil/monstroFacil.model';

@Component({
  selector: 'app-gameplay',
  templateUrl: './gameplay.component.html',
  styleUrls: ['./gameplay.component.scss']
})
export class GameplayComponent {

  constructor(public mecanicasService: MecanicasServiceService) { }

  jogador: Renegado = new Renegado();
  monstro!: MonstroFacil;
  xp: number = 0;
  ataque: number = 0;
  contadorDePartidas: number = 0;

  missaoFacil() {
    this.monstro = this.mecanicasService.partidaFacil();
  }
  missaoCompletaFacil() {
    this.jogador.vida += (this.jogador.vida * 20) / 100;
    this.jogador.xp = this.mecanicasService.missaoCompletaFacil(this.jogador.xp);
    this.contadorDePartidas++;
  }
  missaoCompletaMedia() {
    this.jogador.xp = this.mecanicasService.missaoCompletaMedia(this.jogador.xp);
    this.contadorDePartidas++;
  }
  missaoCompletaDificil() {
    this.jogador.xp = this.mecanicasService.missaoCompletaDificil(this.jogador.xp);
    this.contadorDePartidas++;
  }
  missaoCompletaBoss() {
    this.jogador.xp = this.mecanicasService.missaoCompletaBoss(this.jogador.xp);
    this.contadorDePartidas++;
  }
  xpGasto() {
    this.xp = this.jogador.xp;
    this.jogador.xp = this.mecanicasService.xpGasto(this.jogador.xp);
  }
  uparDeNivel() {
    if (this.mecanicasService.uparDeNivel(this.xp)) {
      this.jogador.nivel++;
      this.jogador.atributos.pontoLivre++
    }
  }

  atacar() {
    this.ataque = this.mecanicasService.atacar(this.jogador.atributos.forca);
    this.monstro.vida -= this.ataque;
    this.ataque = this.mecanicasService.atacar(this.monstro.atributosBase.forca);
    this.jogador.vida -= this.ataque;
    this.mecanicasService.morreu(this.jogador.vida);
    if (this.monstro.vida < 1) this.missaoCompletaFacil();
  }



}
<table>
  <caption>
    Personagem
  </caption>
  <thead>
    <tr>
      <th>Vida</th>
      <th>Mana</th>
      <th>Stamina</th>
      <th>Sanidade</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>{{ jogador.vida }}</td>
      <td>{{ jogador.mana }}</td>
      <td>{{ jogador.stamina }}</td>
      <td>{{ jogador.sanidade }}</td>
    </tr>
    <tr>
      <th colspan="4">Ações</th>
    </tr>
  </tbody>
</table>

<button (click)="missaoCompletaFacil()">MISSAO COMPLETA FACIL</button>
<button (click)="missaoCompletaMedia()">MISSAO COMPLETA MEDIA</button>
<button (click)="missaoCompletaDificil()">MISSAO COMPLETA DIFICIL</button>
<button (click)="missaoCompletaBoss()">MISSAO COMPLETA BOSS</button>

<p style="font-family: monstserrat; font-size: 30px;">{{jogador.xp}}</p>

<button (click)="xpGasto()" (click)="uparDeNivel()">UPAR</button>

<p style="font-family: monstserrat; font-size: 30px;">{{jogador.nivel}}</p>


<h1>LUTAR</h1>

<button (click)="missaoFacil()">Partida Facil</button>

<button (click)="atacar()">Atacar Melee</button>

<p>DANO</p>
<p style="font-family: monstserrat; font-size: 30px;">{{ataque}}</p>
<p *ngIf="monstro.vida">VIDA MONSTRO</p>
<p style="font-family: monstserrat; font-size: 30px;">{{monstro.vida}}</p>
