import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

import { LoginForm } from '../model/loginForms.model';
import { UsuarioDTO } from '../model/usuarioDTO.model';

@Injectable({
  providedIn: 'root'
})
export class UsuarioService {



  constructor(private http: HttpClient) { }

  public logarUsuario(loginForm: LoginForm):
    Observable<UsuarioDTO> {
    let params = new HttpParams();
    let headers = new  HttpHeaders().set('Autorizado', 'Basic dmFsZGlvbm9yanVuaW9yQG91dGxvb2suY29tOkJ3aTI4MDI4MSo=');
    params.set("nome", loginForm.nome);
    params.set("senha", loginForm.senha)
    return this.http.get<UsuarioDTO>(
      "/usuarios/logar", {headers, params}
    )
  }

}
