import { UsuarioDTO } from './../shared/model/usuarioDTO.model';
import { UsuarioService } from './../shared/service/usuario.service';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginForm } from '../shared/model/loginForms.model';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  nomeInput: string = '';
  senhaInput: string = '';
  loginForm: LoginForm = {nome: "", senha: ""};
  usuario!: UsuarioDTO;

  constructor(private router: Router,
    private usuarioService: UsuarioService
  ) {}

  registrarLogar() {
    document.getElementById("background1").style.marginLeft = "-100%";
  }

  entrar() {
    console.log(this.loginForm)
    this.usuarioService.logarUsuario(this.loginForm).subscribe({
      error: () => alert("Tente mais tarde"),
      next: (dado) => {
        this.usuario = dado;
        console.log(this.usuario)
      }
    })
  }

}
