@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'RpgGaming';
}
<div id="loading">
  <h2>
    Carregando
  </h2>
  <img src="assets/img/boonfireMeioLoading.jpg">
</div>
<div class="conteudo">
  <router-outlet></router-outlet>
</div>
