import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {


}
<div class="background">
  <img src="../../../assets/img/bloodbornSelecaoClasseRaca.jpg">
  <div class="gradient">
    <h1 class="introducao" style="margin-top: 5%;">Seja bem-vindo ao Chaos Trials</h1>
    <h3 class="introducao">Aonde sua sorte esta em jogo <br> Sua dificuldade é hardcore, <br>
    mas não desanime, <br>pois sua vitória um dia chegará.</h3>
    <h3 class="introducao">Cadastre-se já</h3>
  </div>
  <div class="telaDeCadastro">
    <input type="text">
  </div>
</div>
