import { Component } from '@angular/core';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {


  warriorBackground() {
    document.getElementById('selecao')!.style.width = "300px";
  }

}
<div class="background" id="selecao">
  <img style="width: 100%;" src="assets/img/classes.webp">
</div>

<div class="warrior"></div>
