import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class MecanicasServiceService {

  constructor() { }

  missaoCompletaFacil(xp:number) {
    return xp += Math.floor(Math.random()*12)
  }

  missaoCompletaMedia(xp:number) {
    return xp += Math.floor(Math.random()*10)
  }

  missaoCompletaDificil(xp:number) {
    return xp += Math.floor(Math.random()*4)
  }

  missaoCompletaBoss(xp:number) {
    return xp += Math.floor(Math.random()*25)
  }

  uparDeNivel(xp: number): boolean {
    if(xp>99) {
      xp = xp - 100;
      return true;
    } else return false;
  }

  uparAtributo(pontoLivre: number) {
    if(pontoLivre > 0) return true;
    else return false;
  }

  morreu(hp: number) {
    if(hp < 1) {
      let i: number;
      let valor: number;
      for(i = 0; i < 3; i++) {
        valor = Math.floor(Math.random()*20);
        if(valor > 18) {
          return false;
        }
      }
      return true;
    } else return false;
  }

}
