import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class MecanicasServiceService {

  valorRequesitadoParaUpar: number = 100;

  constructor() { }

  missaoCompletaFacil(xp:number) {
    return xp += Math.floor(Math.random()*(12 - 6) + 6)
  }

  missaoCompletaMedia(xp:number) {
    return xp += Math.floor(Math.random()*(8 - 4) + 4)
  }

  missaoCompletaDificil(xp:number) {
    return xp += Math.floor(Math.random()*(4 - 2) + 2)
  }

  missaoCompletaBoss(xp:number) {
    return xp += Math.floor(Math.random()*(30 - 15) + 15)
  }

  uparDeNivel(xp: number): boolean {
    if(xp >= this.valorRequesitadoParaUpar) {
      xp = xp - valorRequesitadoParaUpar;
      return true;
    } else return false;
  }

  uparAtributo(pontoLivre: number) {
    if(pontoLivre > 0) return true;
    else return false;
  }

  morreu(hp: number) {
    if(hp < 1) {
      let i: number;
      let valor: number;
      for(i = 0; i < 3; i++) {
        valor = Math.floor(Math.random()*20);
        if(valor > 18) {
          return false;
        }
      }
      return true;
    } else return false;
  }

}
