import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {

  nomeInput: string = '';
  senhaInput: string = '';

  constructor(public router: Router) {}

  entrar() {
    if(this.nomeInput == 'admin' && this.senhaInput == 'admin') this.router.navigate(['jogar'])
  }

}
<div class="background">
  <img src="../../../assets/img/bloodbornSelecaoClasseRaca.jpg">
  <div class="gradient">
    <h1 class="introducao" style="margin-top: 5%; margin-bottom: 2%;">Seja bem-vindo ao Chaos Trials</h1>
    <h3 class="introducao">Aonde sua sorte esta em jogo <br> Sua dificuldade é hardcore, <br>
    mas não desanime, <br>pois sua vitória um dia chegará.</h3>
    <h3 class="introducao">Cadastre-se já</h3>
  </div>
  <div class="telaDeCadastro">
    <h2 style="font-weight: 300; margin-bottom: 20%;">Login</h2>
    <label for="nomeInput"><h3 style="font-weight: 300;">Nome</h3></label>
    <input name="nomeInput" id="nomeInput" type="text" [(ngModel)]="nomeInput">
    <label for="senhaInput"><h3 style="font-weight: 300;">Senha</h3></label>
    <input name="senhaInput" id="senhaInput" type="text" [(ngModel)]="senhaInput">
    <button id="buttonEntrar"><h2 id="textEntrar" style="font-weight: 500;" (click)="entrar()">Entrar</h2></button>
  </div>
</div>
