import { Component } from '@angular/core';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {


  warriorBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
  }
  templarioBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-5%"
  }
  arcanoBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-10%"
  }
  ladinoBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-14%"
  }
  druidaBackground() {
    document.getElementById('selecao')!.style.width = "120%";
    document.getElementById('selecao')!.style.marginTop = "-10%"
    document.getElementById('selecao')!.style.marginLeft = "-20%"
  }

  voltarNormalBackground() {
    document.getElementById('selecao')!.style.width = "100%";
    document.getElementById('selecao')!.style.marginTop = "0%"
    document.getElementById('selecao')!.style.marginLeft = "0%"
  }

}
<div class="background">
  <img id="selecao" style="width: 100%;" src="assets/img/classes.webp">
</div>

<div class="warrior" (mouseenter)="warriorBackground()" (mouseleave)="voltarNormalBackground()"></div>
<div class="templario" (mouseenter)="templarioBackground()" (mouseleave)="voltarNormalBackground()"></div>
<div class="arcano" (mouseenter)="arcanoBackground()" (mouseleave)="voltarNormalBackground()"></div>
<div class="ladino" (mouseenter)="ladinoBackground()" (mouseleave)="voltarNormalBackground()"></div>
<div class="druida" (mouseenter)="druidaBackground()" (mouseleave)="voltarNormalBackground()"></div>


<div id="warrior" class="marcadorDeRosto"></div>
