import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-bonecos',
  templateUrl: './bonecos.component.html',
  styleUrls: ['./bonecos.component.scss']
})
export class BonecosComponent {

  nomeInput: string = '';
  senhaInput: string = '';

  constructor(public router: Router) {}

  entrar() {
    if(this.nomeInput == 'admin' && this.senhaInput == 'admin') this.router.navigate(['jogar'])
  }

}
<div class="background">
  <img src="../../../assets/img/bloodbornSelecaoClasseRaca.jpg">
  <div class="gradient">
    <h1 class="introducao" style="margin-top: 5%; margin-bottom: 2%;">Seja bem-vindo ao <span
        style="color: rgb(255, 75, 111);">Chaos</span> Trials</h1>
    <h3 class="introducao">Aonde sua sorte está em jogo <br> Sua dificuldade é <span
        style="color: rgb(255, 75, 111);">hardcore</span>, <br>
      mas não desanime, <br>pois sua <span style="color: rgb(255, 75, 111);">vitória</span> um dia chegará.</h3>
    <h3 class="introducao">Cadastre-se já</h3>
  </div>
  <div class="telaDeCadastro">
    <h2 style="font-weight: 300; margin-bottom: 20%;">Login</h2>
    <label for="nomeInput">
      <h3 style="font-weight: 300;">Nome</h3>
    </label>
    <input name="nomeInput" id="nomeInput" type="text" autocomplete="off" [(ngModel)]="nomeInput">
    <label for="senhaInput">
      <h3 style="font-weight: 300;">Senha</h3>
    </label>
    <input name="senhaInput" id="senhaInput" type="text" autocomplete="off" [(ngModel)]="senhaInput">
    <button (click)="entrar()" id="buttonEntrar">
      <h2 id="textEntrar" style="font-weight: 500;">Entrar</h2>
    </button>
  </div>
</div>
